package strings;

public class FindDifference {

	public static char findTheDifference(String str1, String str2) {

//		int val = (int) '1' + 48;
//		System.out.println((char) val);
		int countS1 = 0;
		int countS2 = 0;

		for (int i = 0; i < str1.length(); i++) {
			countS1 += (int) str1.charAt(i);
			countS2 += (int) str2.charAt(i);
		}
    
    // Since str2 is one character length is greater, hence adding the last character
		countS2 += (int) str2.charAt(str2.length() - 1);
		return (char) (countS2 - countS1);
	}

	public static void main(String[] args) {
		System.out.println(findTheDifference("abcd", "abcde"));
		System.out.println(findTheDifference(
				"",
				""));
	}
}
